mapper.select:

SELECT IFNULL(AVG(rating),0)
FROM product_review
WHERE product_id = #{productId}

mapper.update:
UPDATE product_review
SET content = #{content},
    rating = #{rating},
    updated_at = NOW()
WHERE id = #{id}
AND member_id = #{memberId}

mapper.delete:
DELETE FROM product_review
WHERE id = #{id}
AND member_id = #{memberId}
